mapper.select:
WITH product_size AS( SELECT op.product_order_code AS order_code, op.product_code AS product_code, op.product_color AS product_color, 're' || get_comm(op.product_size) AS forpvt, (op.qy - COALESCE(SUM(dd.delivery_qy), 0)) AS request_qy FROM order_product op LEFT JOIN delivery_details dd ON op.product_order_code = dd.order_code AND op.product_code = dd.product_code AND op.product_color = dd.product_color AND op.product_size = dd.product_size WHERE op.product_order_code = #{orderCode} AND op.product_code = #{productCode} GROUP BY op.product_order_code, op.product_code, op.product_color, op.product_size, op.qy) SELECT * FROM product_size pivot( sum(request_qy) FOR forpvt IN (
'${'re' + size.showSize}' AS ${'re' + size.showSize}
) )pvt

SELECT COUNT(*) AS time
FROM delivery
WHERE product_order_code = #{productOrderCode}

mapper.update:
UPDATE order_list
SET    order_status = #{info.orderStatus}
WHERE  product_order_code = #{info.productOrderCode}

mapper.select:
SELECT lot AS productLot,
	   inventory_qy AS lotQy
FROM   inventory
WHERE  product_se = 'PT02'
AND    product_code = #{pinfo.productCode}
AND    product_color = #{pinfo.productColor}
AND    product_size = #{pinfo.productSize}
AND    inventory_qy > 0

mapper.update:
UPDATE inventory
SET inventory_qy = inventory_qy - #{deInfo.deliveryQy}
WHERE product_code = #{deInfo.productCode}
AND   product_color = #{deInfo.productColor}
AND   product_size = #{deInfo.productSize}

mapper.select:
select unit_price AS unit_price,
       unit_price * #{pinfo.deliveryQy} AS price
from order_product
where product_order_code = #{pinfo.productOrderCode}
and   product_code = #{pinfo.productCode}
and   product_size = #{pinfo.productSize}
and   product_color = #{pinfo.productColor}
