mapper.select:
SELECT COUNT(*)+1 AS time
FROM delivery
WHERE product_order_code = #{productOrderCode}

mapper.update:
UPDATE order_list
SET    order_status = #{info.orderStatus}
WHERE  product_order_code = #{info.productOrderCode}

mapper.select:
SELECT lot AS productLot,
	   inventory_qy AS lotQy
FROM   inventory
WHERE  product_se = 'PT02'
AND    product_code = #{pinfo.productCode}
AND    product_color = #{pinfo.productColor}
AND    product_size = #{pinfo.productSize}
AND    inventory_qy > 0

mapper.update:
UPDATE inventory
SET inventory_qy = inventory_qy - #{deInfo.deliveryQy}
WHERE product_code = #{deInfo.productCode}
AND   product_color = #{deInfo.productColor}
AND   product_size = #{deInfo.productSize}

mapper.select:
select unit_price AS unit_price,
       unit_price * #{pinfo.deliveryQy} AS price
from order_product
where product_order_code = #{pinfo.productOrderCode}
and   product_code = #{pinfo.productCode}
and   product_size = #{pinfo.productSize}
and   product_color = #{pinfo.productColor}

WITH product_size AS( SELECT dd.product_code AS product_code, dd.product_color AS product_color, 'de' || get_comm(dd.product_size) AS forpvt, dd.delivery_qy AS qy FROM delivery de JOIN delivery_details dd ON (de.delivery_code = dd.delivery_code) WHERE de.delivery_code = #{deliveryCode} AND dd.product_code = #{productCode} AND dd.delivery_qy > 0) SELECT * FROM product_size pivot( sum(qy) FOR forpvt IN (
'${"de" + size.showSize}' AS ${"de" + size.showSize}
) )pvt

SELECT product_lot,
       delivery_qy
FROM   delivery_details
WHERE  delivery_code = #{info.deliveryCode}
AND    product_code = #{info.productCode}
AND    product_size = #{info.productSize}
AND    product_color = #{info.productColor}
