mapper.select:
SELECT * FROM( SELECT * FROM ( SELECT ROWNUM AS rn, get_address(de.company_code) AS dedt_address, de.company_name AS company_name, CASE WHEN de.delivery_status = 'DS05' THEN '완료' ELSE '미완료' END AS delivery_status, TO_CHAR(de.dedt, 'yyyy-MM-dd') AS dedt, de.delivery_code AS delivery_code FROM delivery de WHERE de.delivery_company_code IS NULL
AND get_address(de.company_code) LIKE '%' || #{search.dedtAddress} || '%'
AND de.company_name LIKE '%' || #{search.companyName} || '%'
AND de.delivery_status IN ('DS03', 'DS05')
AND de.delivery_status = #{search.deliveryStatus}
AND de.delivery_status IN ('DS03')
)info WHERE info.rn <= #{search.end})page WHERE page.rn >= #{search.start}

mapper.update:
UPDATE delivery_schedule
SET    driver_code = #{info.chargerCode}, driver_name = #{info.chargerName}
WHERE  delivery_code = #{info.deliveryCode}

UPDATE delivery
SET delivery_status = 'DS05'
WHERE delivery_code = #{deliveryCode}
