SELECT * FROM ( SELECT * FROM ( SELECT ROWNUM rn, mtril_order_code AS mtrilOrderCode, product_summary('material_order_details', mtril_order_code) AS Summary, charger_name AS orderCharger, rgsde AS orderDate, due_date AS dedt, remark AS remark FROM material_order WHERE status = 'RS01' --주문코드 AND mtril_order_code LIKE '%' || UPPER(#{searchDto.orderCode}) || '%'
--발주담당자 AND charger_name LIKE '%' || #{searchDto.orderCharger} || '%'
--제품코드 AND mtril_order_code IN (SELECT mtril_order_code FROM material_order_details WHERE product_code LIKE '%' || UPPER(#{searchDto.productCode}) || '%')
--제품명 AND mtril_order_code IN (SELECT m.mtril_order_code FROM material_order_details m JOIN product_info s ON (m.product_code = s.product_code) WHERE s.product_name LIKE '%' || #{searchDto.productName} || '%')
AND rgsde BETWEEN #{searchDto.orderDateStart} AND #{searchDto.orderDateEnd}
AND due_date BETWEEN #{searchDto.dedtStart} AND #{searchDto.dedtEnd}
) info WHERE info.rn <= 10) page WHERE page.rn >= 1

SELECT COUNT(*) FROM material_order WHERE status = 'RS01' --주문코드 AND mtril_order_code LIKE '%' || #{searchDto.orderCode} || '%'
--발주담당자 AND charger_name LIKE '%' || #{searchDto.orderCharger} || '%'
--제품코드 AND mtril_order_code IN (SELECT mtril_order_code FROM material_order_details WHERE product_code LIKE '%' || #{searchDto.productCode} || '%')
--제품명 AND mtril_order_code IN (SELECT m.mtril_order_code FROM material_order_details m JOIN product_info s ON (m.product_code = s.product_code) WHERE s.product_name LIKE '%' || #{searchDto.productName} || '%')
AND rgsde BETWEEN #{searchDto.orderDateStart} AND #{searchDto.orderDateEnd}
AND due_date BETWEEN #{searchDto.dedtStart} AND #{searchDto.dedtEnd}
