mapper.select:
		with product as
          (SELECT product_color,
                  'sz' || get_comm(product_size) as product_size_info,
                  NVL(inventory_qy,0) as inventory_qy
           FROM inventory
           WHERE product_code = #{productCode})
    	select * from product
		pivot( sum(inventory_qy)
       		for product_size_info in (

       			'${'sz'+size.productSizeInfo}' AS ${'sz'+size.productSizeInfo}
       		)
		)pvt

SELECT * FROM ( SELECT a.*, ROWNUM as rn FROM ( SELECT MAX(i.product_code) AS product_code, p.product_name, i.product_color, get_comm(i.product_size) AS product_size, i.lot, i.storage_location, i.inventory_qy, i.store_date, s.storage_info_name as storageName FROM inventory i JOIN product_info p ON i.product_code = p.product_code join storage_info s on i.storage_location = s.storage_info_code
AND product_se = 'PT02' AND i.inventory_qy > 0
AND i.product_code LIKE '%' || UPPER(#{productCode}) || '%'
AND p.product_name LIKE '%' || UPPER(#{productName}) || '%'
AND i.product_color LIKE '%' || UPPER(#{searchColor}) || '%'
AND i.storage_location LIKE '%' || UPPER(#{location}) || '%'
AND i.lot LIKE '%' || UPPER(#{searchLot}) || '%'
AND i.product_size LIKE '%' || UPPER(#{sizeCode}) || '%'
AND TRUNC(i.store_date) >= #{startStoreDate}
AND TRUNC(i.store_date) >= #{endStoreDate}
AND TRUNC(i.store_date) BETWEEN #{startStoreDate} and #{endStoreDate}
GROUP BY i.lot, p.product_name, i.product_color, get_comm(i.product_size), i.storage_location, i.inventory_qy, i.product_code, i.store_date,s.storage_info_name ORDER BY lot desc ) a WHERE ROWNUM <= #{end} ) page WHERE page.rn >= #{start}

mapper.update:
UPDATE inventory
SET inventory_qy = inventory_qy - #{errorQy}
WHERE product_color = #{productColor}
AND product_size = #{productSize}
AND product_code = #{productCode}
AND lot = #{lot}

UPDATE inventory
 SET storage_location = #{storageLocation}
WHERE lot = #{lot}
