mapper.select:
		with product as
          (SELECT product_color,
                  UPPER(get_comm(product_size)) as product_size,
                  inventory_qy
           FROM inventory
           WHERE product_code = #{productCode})
    	select * from product
		pivot( sum(inventory_qy)
       		for product_size in (

       			'${size.productSizeInfo}' AS ${size.productSizeInfo}
       		)
		)pvt
