mapper.select:
		with temp as
		     (SELECT   product_color,
		               UPPER(get_comm(product_size)) as product_size,
		               product_code,
		               delivery_qy
		        FROM DELIVERY_DETAILS
		        WHERE product_code = #{productCode})
		select * from temp
		pivot( sum(delivery_qy)
		       for product_size in (

                '${size.productSizeInfo}' AS ${size.productSizeInfo}
               )
		)pvt

mapper.update:
		UPDATE delivery_details
        SET delivery_qy = delivery_qy - #{productSizeInfo}
        WHERE product_code = #{productCode}
        AND product_size = #{productSize}
        AND product_color = #{productColor}
