mapper.select:
		with temp as
		     (SELECT   product_color,
		               'sz' || get_comm(product_size) as product_size,
		               product_code,
		               delivery_qy
		        FROM DELIVERY_DETAILS
		        WHERE product_code = #{productCode}
		        AND delivery_code = #{deliveryCode})
		select * from temp
		pivot( sum(delivery_qy)
		       for product_size in (

                '${'sz' + size.productSizeInfo}' AS ${'sz' + size.productSizeInfo}
               )
		)pvt

mapper.update:
		UPDATE delivery_details
        SET delivery_qy = delivery_qy - #{productQy}
        WHERE product_code = #{productCode}
        AND product_size = #{sizeCode}
        AND product_color = #{color}
        AND delivery_code = #{deliveryCode}
