mapper.select:
SELECT get_comm(product_size) as product_size
FROM product_option
WHERE product_code = #{productCode}

		with temp as
		     (SELECT   product_color,
		               product_size,
		               request_qy
		        FROM DELIVERY_DETAILS
		        WHERE product_code = #{productCode})
		select * from temp
		pivot( sum(request_qy)
		       for product_size in (

                '${size.productSize}' AS ${size.productSize}
               )
		)pvt
