mapper.select:
SELECT COUNT(DISTINCT(c.company_code)) FROM company c JOIN address_list a ON c.address_seq = a.address_seq JOIN material_of_company m ON m.company_code = c.company_code LEFT JOIN product_option o ON o.option_code = m.option_code WHERE c.section = 'CP01' AND m.discontinued = 'YN02' AND UPPER(c.company_code) LIKE '%' || UPPER(#{companyCode}) || '%' AND UPPER(c.company_name) LIKE '%' || UPPER(#{companyName}) || '%'
AND '(' || a.address_number || ')' || a.address_main || ' ' || a.address_info || a.address_reference LIKE '%' || #{address} || '%'
AND m.product_code = #{productCode} AND o.product_color = #{productColor}

SELECT COUNT(DISTINCT(p.product_code))
FROM   product_info p LEFT JOIN  product_category c ON p.product_code = c.product_code
                      JOIN material_of_company m ON p.product_code = m.product_code
WHERE  p.product_type = 'PT01'
  AND  m.discontinued = 'YN02'
 AND c.lclas = #{lclas}	 
 AND c.sclas = #{sclas}	 
 AND UPPER(p.product_code) LIKE '%' || UPPER(#{productCode}) || '%'  
 AND UPPER(p.product_name) LIKE '%' || UPPER(#{productName}) || '%'  
 AND m.company_code = #{companyCode}

	SELECT * FROM
	    (SELECT DISTINCT(p.mtril_order_plan_code),
	            p.due_date,
	            product_summary('material_order_plan_details', p.mtril_order_plan_code) summary,
	            (SELECT SUM(order_qy) 
	             FROM   material_order_plan_details
	             WHERE  mtril_order_plan_code = p.mtril_order_plan_code) order_qy,
	            p.remark,
	            p.charger_name,
	            NVL(p.update_date, p.rgsde) update_date,
	            get_comm(p.status) status
	     FROM   material_order_plan p JOIN material_order_plan_details d ON p.mtril_order_plan_code = d.mtril_order_plan_code

	       AND p.status IN (  #{st}  ) 
	       AND d.product_code = #{productCode} 
	       AND UPPER(get_prd_name(d.product_code)) LIKE '%' || UPPER(#{productName}) || '%' 
	     ) -- 서브쿼리 형태로 alias 사용 가능

	   AND UPPER(mtril_order_plan_code) LIKE '%' || UPPER(#{mtrilOrderPlanCode}) || '%' 
	   AND  TO_CHAR(due_date, 'yyyy-mm-dd') >= #{dueMin}  
       AND  TO_CHAR(due_date, 'yyyy-mm-dd') <= #{dueMax}  

       AND  order_qy >= #{qyMin}  
       AND  order_qy <= #{qyMax}  
       AND UPPER(remark) LIKE '%' || UPPER(#{remark}) || '%'  
       AND UPPER(charger_name) LIKE '%' || UPPER(#{chargerName}) || '%'  

       AND  TO_CHAR(update_date 'yyyy-mm-dd') >= #{rgsdeMin}  
       AND  TO_CHAR(update_date, 'yyyy-mm-dd') <= #{rgsdeMax}  

		ORDER  BY mtril_order_plan_code DESC

SELECT COUNT(*) FROM
    (SELECT DISTINCT(p.mtril_order_plan_code),
            p.due_date,
            (SELECT SUM(order_qy) 
             FROM   material_order_plan_details
             WHERE  mtril_order_plan_code = p.mtril_order_plan_code) order_qy,
            p.remark,
            p.charger_name,
            NVL(p.update_date, p.rgsde) update_date
     FROM   material_order_plan p JOIN material_order_plan_details d ON p.mtril_order_plan_code = d.mtril_order_plan_code

       AND p.status IN (  #{st}  ) 
       AND d.product_code = #{productCode} 
       AND UPPER(d.product_name) LIKE '%' || UPPER(#{productName}) || '%' 
     ) -- 바깥에서 추가조건 검색하여 alias 이용 가능

   AND UPPER(mtril_order_plan_code) LIKE '%' || UPPER(#{mtrilOrderPlanCode}) || '%' 
   AND  TO_CHAR(due_date, 'yyyy-mm-dd') >= #{dueMin}  
   AND  TO_CHAR(due_date, 'yyyy-mm-dd') <= #{dueMax}  

   AND  order_qy >= #{qyMin}  
   AND  order_qy <= #{qyMax}  
   AND UPPER(remark) LIKE '%' || UPPER(#{remark}) || '%'  
   AND UPPER(charger_name) LIKE '%' || UPPER(#{chargerName}) || '%'  

   AND  TO_CHAR(update_date 'yyyy-mm-dd') >= #{rgsdeMin}  
   AND  TO_CHAR(update_date, 'yyyy-mm-dd') <= #{rgsdeMax}
