mapper.select:
SELECT COUNT(DISTINCT(c.company_code)) FROM company c JOIN address_list a ON c.address_seq = a.address_seq JOIN material_of_company m ON m.company_code = c.company_code LEFT JOIN product_option o ON o.option_code = m.option_code WHERE c.section = 'CP01' AND m.discontinued = 'YN02' AND UPPER(c.company_code) LIKE '%' || UPPER(#{companyCode}) || '%' AND UPPER(c.company_name) LIKE '%' || UPPER(#{companyName}) || '%'
AND '(' || a.address_number || ')' || a.address_main || ' ' || a.address_info || a.address_reference LIKE '%' || #{address} || '%'
AND m.product_code = #{productCode} AND o.product_color = #{productColor}

SELECT COUNT(DISTINCT(p.product_code))
FROM   product_info p LEFT JOIN  product_category c ON p.product_code = c.product_code
                      JOIN material_of_company m ON p.product_code = m.product_code
WHERE  p.product_type = 'PT01'
  AND  m.discontinued = 'YN02'
 AND c.lclas = #{lclas}	 
 AND c.sclas = #{sclas}	 
 AND UPPER(p.product_code) LIKE '%' || UPPER(#{productCode}) || '%'  
 AND UPPER(p.product_name) LIKE '%' || UPPER(#{productName}) || '%'  
 AND m.company_code = #{companyCode}

SELECT inventory_qy, storing_qy, duration FROM ( SELECT DISTINCT(p.product_code), rownum rn, NVL(( SELECT SUM(i.inventory_qy) -- 현재고량 FROM inventory i WHERE i.product_code = p.product_code ), 0) inventory_qy, NVL(( SELECT SUM(mod.order_qy) -- 아직 처리되지 않은 기발주량 합계 FROM material_order_details mod JOIN material_order mo ON mod.mtril_order_code = mo.mtril_order_code WHERE mo.status IN ('RS01', 'RS02') -- 발주상태: 대기 혹은 부분출고 AND mod.product_code = p.product_code ), 0) storing_qy, (SELECT ROUND(AVG(duration)) FROM (SELECT (TRUNC(TO_DATE( (SELECT MAX(updde) FROM delivery WHERE mtril_order_code = mo.mtril_order_code) )) - TRUNC(rgsde)) duration, mo.mtril_order_code, d.product_code, d.product_color, mo.company_code FROM material_order mo JOIN material_order_details d ON mo.mtril_order_code = d.mtril_order_code WHERE mo.status = 'RS03') -- 발주에서 출고완료 상태까지의 평균값 산출 (7일 범위 이내에서 계산)) WHERE duration <= 7 AND product_code = p.product_code AND company_code = m.company_code) duration FROM product_info p JOIN material_of_company m ON p.product_code = m.product_code WHERE p.product_code = #{productCode}
AND m.company_code = #{companyCode} AND m.discontinued = 'YN02'
ORDER BY duration NULLS LAST ) WHERE rn = 1

SELECT * FROM (SELECT DISTINCT(p.mtril_order_plan_code), p.due_date, product_summary('material_order_plan_details', p.mtril_order_plan_code) summary, (SELECT SUM(order_qy) FROM material_order_plan_details WHERE mtril_order_plan_code = p.mtril_order_plan_code) order_qy, p.remark, p.charger_name, p.rgsde, p.update_date, get_comm(p.status) status FROM material_order_plan p JOIN material_order_plan_details d ON p.mtril_order_plan_code = d.mtril_order_plan_code
AND p.status IN ( #{st} ) AND UPPER(d.product_code) LIKE '%' || UPPER(#{productCode}) || '%' AND UPPER(get_prd_name(d.product_code)) LIKE '%' || UPPER(#{productName}) || '%' AND UPPER(d.company_code) LIKE '%' || UPPER(#{companyCode}) || '%' AND UPPER(d.company_name) LIKE '%' || UPPER(#{companyName}) || '%' ) -- 서브쿼리 형태로 alias 사용 가능
AND UPPER(mtril_order_plan_code) LIKE '%' || UPPER(#{mtrilOrderPlanCode}) || '%' AND TO_CHAR(due_date, 'yyyy-mm-dd') >= #{dueMin} AND TO_CHAR(due_date, 'yyyy-mm-dd') <= #{dueMax}
AND order_qy >= #{qyMin} AND order_qy <= #{qyMax} AND UPPER(remark) LIKE '%' || UPPER(#{remark}) || '%' AND UPPER(charger_name) LIKE '%' || UPPER(#{chargerName}) || '%'
AND TO_CHAR(rgsde, 'yyyy-mm-dd') >= #{rgsdeMin} AND TO_CHAR(rgsde, 'yyyy-mm-dd') <= #{rgsdeMax}
ORDER BY mtril_order_plan_code DESC

SELECT COUNT(*) FROM (SELECT DISTINCT(p.mtril_order_plan_code), p.due_date, (SELECT SUM(order_qy) FROM material_order_plan_details WHERE mtril_order_plan_code = p.mtril_order_plan_code) order_qy, p.remark, p.charger_name, p.rgsde, p.update_date, get_comm(p.status) status FROM material_order_plan p JOIN material_order_plan_details d ON p.mtril_order_plan_code = d.mtril_order_plan_code
AND p.status IN ( #{st} ) AND UPPER(d.product_code) LIKE '%' || UPPER(#{productCode}) || '%' AND UPPER(get_prd_name(d.product_code)) LIKE '%' || UPPER(#{productName}) || '%' AND UPPER(d.company_code) LIKE '%' || UPPER(#{companyCode}) || '%' AND UPPER(d.company_name) LIKE '%' || UPPER(#{companyName}) || '%' ) -- 바깥에서 검색하여 alias 이용 가능
AND UPPER(mtril_order_plan_code) LIKE '%' || UPPER(#{mtrilOrderPlanCode}) || '%' AND TO_CHAR(due_date, 'yyyy-mm-dd') >= #{dueMin} AND TO_CHAR(due_date, 'yyyy-mm-dd') <= #{dueMax}
AND order_qy >= #{qyMin} AND order_qy <= #{qyMax} AND UPPER(remark) LIKE '%' || UPPER(#{remark}) || '%' AND UPPER(charger_name) LIKE '%' || UPPER(#{chargerName}) || '%'
AND TO_CHAR(rgsde, 'yyyy-mm-dd') >= #{rgsdeMin} AND TO_CHAR(rgsde, 'yyyy-mm-dd') <= #{rgsdeMax}

mapper.update:
UPDATE material_order
update_date = SYSDATE, due_date = #{dueDate}, remark = #{remark}, status = #{status},
WHERE mtril_order_code = #{mtrilOrderCode}

UPDATE material_order_details
SET    order_qy = #{orderQy}
WHERE  mtril_order_code = #{mtrilOrderCode}
  AND  product_code = #{productCode}
  AND  
          product_color IS NULL
          product_color = #{productColor}

UPDATE material_order_plan
update_date = SYSDATE, due_date = #{dueDate}, remark = #{remark}, status = #{status},
WHERE mtril_order_plan_code = #{mtrilOrderPlanCode}

UPDATE material_order_plan_details
SET    order_qy = #{orderQy}
WHERE  mtril_order_plan_code = #{mtrilOrderPlanCode}
  AND  product_code = #{productCode}
  AND  
          product_color IS NULL
          product_color = #{productColor}
